function helloWorld () { alert(&#34;Hello, World!&#34;) } public class HelloWorld { public static void main(String[] args) { System.out.println(&#34;Hello, World!&#34;); } } package hello fun main(args: Array&lt;String&gt;) { println(&#34;Hello World!&#34;) } #include &lt;stdio.h&gt; /* Hello */ int main(void){ printf(&#34;Hello, World!&#34;); return 0; } // &#39;Hello World!&#39; program  #include &lt;iostream&gt; int main(){ std::cout &lt;&lt; &#34;Hello World!&#34; &lt;&lt; std::endl; return 0; } using System; class HelloWorld{ public static void Main(){ System.Console.WriteLine(&#34;Hello, World!&#34;); } } &lt;html&gt; &lt;body&gt; Hello, World! &lt;/body&gt; &lt;/html&gt; package main import fmt &#34;fmt&#34; func main() { fmt.Printf(&#34;Hello, World!\n&#34;); } object HelloWorld with Application { Console.println(&#34;Hello, World!&#34;); } &lt;?php echo &#39;Hello, World!&#39;; ?&gt; print(&#34;Hello, World!&#34;) (defn hello-world &#34;A function print &#39;Hello world&#39;.&#34; [] (prn &#34;Hello world&#34;)) &lt;!DOCTYPE html&gt; &lt;html&gt; &lt;head&gt; &lt;title&gt;{{ .Title }}&lt;/title&gt; &lt;/head&gt; &lt;body&gt; &lt;h1&gt;{{ .Title }}&lt;/h1&gt; {{ .Content }} &lt;/body&gt; &lt;/html&gt; {{ partial &#34;header.html&#34; . }} &lt;h1&gt;posts&lt;/h1&gt; {{ range first 10 .Data.Pages }} {{ if eq .Type &#34;post&#34;}} &lt;h2&gt;&lt;a href=&#34;{{ .Permalink }}&#34;&gt;{{ .Title }}&lt;/a&gt;&lt;/h2&gt; {{ end }} {{ end }} &lt;h1&gt;pages&lt;/h1&gt; {{ range .Data.Pages }} {{ if or (eq .Type &#34;page&#34;) (eq .Type &#34;about&#34;) }} &lt;h2&gt;&lt;a href=&#34;{{ .Permalink }}&#34;&gt;{{ .Type }} - {{ .Title }} - {{ .RelPermalink }}&lt;/a&gt;&lt;/h2&gt; {{ end }} {{ end }} {{ partial &#34;footer.html&#34; . }}  Detect the language
